package mrm.codeOperation;import java.awt.*;import java.awt.event.*;import store.Card;import store.Movies;import mrm.MRM;     class GUI extends Panel{         GUI(OptOperatorPanel operatorPanel,        OptCardReader cardReader,        OptDisplay display,        OptKeyboard keyboard,        OptMovieDispenser movieDispenser,        OptEnvelopeAcceptor envelopeAcceptor,        OptReceiptPrinter receiptPrinter)    {        setBackground(new Color(195,232,250));                              mainLayout = new CardLayout(5,5);        setLayout(mainLayout);                atmPanel = new MRMPanel(this,                                 operatorPanel, cardReader,                                 display, keyboard,                                movieDispenser, envelopeAcceptor, receiptPrinter);        this.add(atmPanel, "MRM");        billsPanel = new MoviePanel();        add(billsPanel, "BILLS");                cardPanel = new CardPanel();        add(cardPanel, "CARD");        logPanel = new LogPanel(this);        add(logPanel, "LOG");        mainLayout.show(this, "MRM");    }           public Movies movieinventory()    {        mainLayout.show(this, "BILLS");        int numberOfBills = billsPanel.readMovies();        mainLayout.show(this, "MRM");                return new Movies(20 * numberOfBills);    }           public Card readCard()    {        mainLayout.show(this, "CARD");        int cardNumber = cardPanel.readCardNumber();        mainLayout.show(this, "MRM");                if (cardNumber > 1000)            return new Card(cardNumber);        else            return null;    }           public void printLogLine(String text)    {        logPanel.println(text);    }          void showCard(String cardName)    {        mainLayout.show(this, cardName);    }          static GridBagConstraints makeConstraints(                    int row, int col, int width, int height, int fill)    {         GridBagConstraints g = new GridBagConstraints();        g.gridy = row;        g.gridx = col;        g.gridheight = height;        g.gridwidth = width;        g.fill = fill;        g.insets = new Insets(2,2,2,2);        g.weightx = 1;        g.weighty = 1;        g.anchor = GridBagConstraints.CENTER;        return g;    }        private CardLayout mainLayout;          private MRMPanel atmPanel;     private MoviePanel billsPanel;          private CardPanel cardPanel;          private LogPanel logPanel;}    