 package mrm.codeOperation;import java.awt.*;import java.awt.event.*;import store.Card;import mrm.MRM;class OptCardReader extends Button{      OptCardReader(final Operations operations)    {        super("Click to Insert card");        addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e)            {                operations.cardInserted();            }        });                               setVisible(true);    }          void animateInsertion()    {                       setVisible(false);        try        {        	Thread.sleep(1000);        }catch(Exception e)        {        	        }        setVisible(true);        setLabel("Card in process");    }            void animateEjection()    {        setLabel("Ejected card");        setVisible(true);                           try             {                 Thread.sleep(1000);            }             catch (InterruptedException e)             { }             setLabel("Click to Insert card");    }       void animateRetention()    {        setLabel("Click to Insert card");        setVisible(true);    }    }                                       