package mrm.codeOperation;import java.awt.*;import java.awt.event.*;import java.util.StringTokenizer;class OptDisplay extends Panel{        OptDisplay()    {        setLayout(new GridLayout(MRMPanel.DISPLAYABLE_LINES, 1));        setBackground(Color.white);          setForeground(Color.black);        Font lineFont = new Font("Monospaced", Font.PLAIN, 14);        displayLine = new Label[MRMPanel.DISPLAYABLE_LINES];        for (int i = 0; i < MRMPanel.DISPLAYABLE_LINES; i ++)        {            displayLine[i] = new Label(MRMPanel.BLANK_DISPLAY_LINE);            displayLine[i].setFont(lineFont);            add(displayLine[i]);        }        currentDisplayLine = 0;            }           void clearDisplay()    {         for (int i = 0; i < displayLine.length; i ++)            displayLine[i].setText("");        currentDisplayLine = 0;    }            void display(String text)    {         StringTokenizer tokenizer = new StringTokenizer(text, "\n", false);        while (tokenizer.hasMoreTokens())        {             try            {                 displayLine[currentDisplayLine ++].setText(tokenizer.nextToken());             }            catch (Exception e)            { }        }    }     void setEcho(String echo)    {        displayLine[currentDisplayLine].setText(            MRMPanel.BLANK_DISPLAY_LINE.substring(0,                 MRMPanel.BLANK_DISPLAY_LINE.length() / 2 - echo.length()) + echo);    }          public Insets getInsets()    {        Insets insets = super.getInsets();        insets.top += 5;        insets.bottom += 5;        insets.left += 10;        insets.right += 10;        return insets;    }       private Label [] displayLine;           private int currentDisplayLine;}                                       