package mrm.codeOperation;import java.awt.*;import java.awt.event.*;class OptEnvelopeAcceptor extends Button{    OptEnvelopeAcceptor()    {        super("Click to Return movie");        addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e)            {                synchronized(OptEnvelopeAcceptor.this)                {                    inserted = true;                    OptEnvelopeAcceptor.this.notify();                }            }        });    }            public synchronized boolean acceptEnvelope()    {        inserted = false;        setVisible(true);                           try        {             wait(MAXIMUM_WAIT_TIME);        }        catch(InterruptedException e)        { }                if (inserted)        {                                   setVisible(false);                      try            {            	Thread.sleep(1000);            }catch(Exception e)            {            	            }            setVisible(true);        }        else        {            setVisible(false);        }        return inserted;    }       public synchronized void cancelRequested()    {        notify();           }           private boolean inserted;            private static long MAXIMUM_WAIT_TIME = 20 * 1000;}                                       