package mrm.codeOperation;import java.awt.*;import java.awt.event.*;import mrm.MRM;class OptOperatorPanel extends Panel{       OptOperatorPanel(final Operations operations)    {        setLayout(new BorderLayout(10, 0));        setBackground(new Color(128,128,255));        add(new Label("PLEASE TURN ON/OFF THE MACHINE"), BorderLayout.NORTH);        final Label message = new Label("PLEASE TURN OFF/ON THE MACHINE ", Label.CENTER);        add(message, BorderLayout.CENTER);        final Button button = new Button(" ON ");        button.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e)            {                if (button.getLabel().equals("OFF"))                    {                    message.setText("PLEASE TURN OFF THE MACHINE ");                    button.setLabel(" ON ");                                        operations.switchChanged(false);                }                else                                                  {                    message.setText("PLEASE TURN ON THE MACHINE ");                    button.setLabel("OFF");                                        operations.switchChanged(true);                }            }        });        Panel buttonPanel = new Panel();        buttonPanel.add(button);        add(buttonPanel, BorderLayout.CENTER);                   }}                                       