package mrm.movietransaction;import store.AccountInformation;import store.Card;import store.Message;import store.Movies;import store.Receipt;import store.Status;import mrm.MRM;import mrm.Session;import mrm.physical.*;public class Hold extends Transaction{    public Hold(MRM mrm, Session session, Card card, int zip)    {        super(mrm, session, card, zip);    }          protected Message getSpecificsFromCustomer() throws CustomerConsole.Cancelled    {             from = mrm.getCustomerConsole().readMenuChoice(                "Account to withdraw from",                AccountInformation.ACCOUNT_NAMES);        	String [] amountOptions = { "Avatar", "Wanted", "Do or Die", "Superman", "Fast5" };            Movies [] amountValues = {                                       new Movies(5), new Movies(5), new Movies(5),                                      new Movies(5), new Movies(5)                                    };                                                  String amountMessage = "";            boolean validAmount = false;                        while (! validAmount)            {                amount = amountValues [                     mrm.getCustomerConsole().readMenuChoice(                        amountMessage + "Movies to hold", amountOptions) ];                                                validAmount = mrm.getMovieDispenser().checkMovieOnHand(amount);                if (! validAmount)                    amountMessage = "Movies Unavailable\n";            }                        return new Message(Message.HOLD,                                card, zip, serialNumber, from, -1, amount);    }            protected Receipt completeTransaction()    {        return new Receipt(this.mrm, this.card, this, this.balances) {            {                detailsPortion = new String[2];                detailsPortion[0] = "HOLD CRETERIA : " +                                     AccountInformation.ACCOUNT_ABBREVIATIONS[to] ;                detailsPortion[1] = "MOVIE WORTH : " + amount.toString();            }        };    }            private int from;            private int to;           private Movies amount;           }