 package mrm.physical;import mrm.MRM;import mrm.codeOperation.Operations;import store.Card;public class CardReader{       public CardReader(MRM mrm)    {        this.mrm = mrm;    }           public Card readCard()    {        return Operations.getInstance().readCard();    }           public void ejectCard()    {        Operations.getInstance().ejectCard();    }           public void retainCard()    {        Operations.getInstance().retainCard();    }            private MRM mrm;    }