package mrm.physical;import mrm.codeOperation.Operations;import store.Message;import store.Movies;import store.Status; public class Log{        public Log()    {    }         public void logSend(Message message)    {        Operations.getInstance().printLogLine("Message:   " + message.toString());     }         public void logResponse(Status response)    {        Operations.getInstance().printLogLine("Response:  " + response.toString());    }          public void logMovieDispensed(Movies amount)    {        Operations.getInstance().printLogLine("CHARGE FOR RENT: " + amount.toString());    }          public void logMovieAccepted()    {        Operations.getInstance().printLogLine("Envelope:  received");    }}