package mrm.physical;import mrm.codeOperation.Operations; public class MovieAcceptor{     public MovieAcceptor(Log log)    {        this.log = log;    }           public void acceptEnvelope() throws CustomerConsole.Cancelled    {        boolean inserted = Operations.getInstance().acceptEnvelope();        if (inserted)            log.logMovieAccepted();        else            throw new CustomerConsole.Cancelled();    }            private Log log;}