package mrm.physical;import mrm.codeOperation.Operations;import store.Movies; public class MovieDispenser{       public MovieDispenser(Log log)    {        this.log = log;                movieOnHand = new Movies(0);    }           public void setInventory(Movies movieinventory)    {        movieOnHand = movieinventory;    }            public boolean checkMovieOnHand(Movies amount)    {        return amount.lessEqual(movieOnHand);    }            public void dispenseMovie(Movies amount)    {        movieOnHand.subtract(amount);                              log.logMovieDispensed(amount);    }        private Log log;           private Movies movieOnHand;}