 package mrm.physical;import java.net.InetAddress;import mrm.codeOperation.Operations;import store.Balances;import store.Message;import store.Status;public class NetworkToCenter{      public NetworkToCenter(Log log, InetAddress storeAddress)    {        this.log = log;        this.storeAddress = storeAddress;    }           public void openConnection()    {            }            public void closeConnection()    {          }          public Status sendMessage(Message message, Balances balances)    {                        log.logSend(message);                                Status result = Operations.getInstance().sendMessage(message, balances);                                log.logResponse(result);                return result;    }                private Log log;              private InetAddress storeAddress;}