package store;public class Message{      public Message(int messageCode, Card card, int zip,                    int serialNumber, int fromAccount, int toAccount, Movies amount)    {        this.messageCode = messageCode;        this.card = card;        this.zip = zip;        this.serialNumber = serialNumber;        this.fromAccount = fromAccount;        this.toAccount = toAccount;        this.amount = amount;    }        public Message(int transfer2, Card card2, int zip2, int serialNumber2,			int from, int to, String amount2) {			}	    public String toString()    {        String result = "";                switch (messageCode)        {            case RENT:                            result += "RENTED MOVIE ";                break;                            case INITIATE_RETURN:                            result += "INIT_DEPOSIT MOVIE ";                break;                            case COMPLETE_RETURN:                            result += "COMP_DEPOSIT MOVIE ";                break;                            case HOLD:                            result += "HOLD MOVIE ";                break;                            case INQUIRY:                            result += "INQUIRY ";                break;        }                result += " CARD#" + card.getNumber()+" ";        result += " TRANS#" + serialNumber+" ";               if (! amount.lessEqual(new Movies(0)))            result += "CHARGE " + amount;        else            result += " NO AMOUNT";                return result;    }                        public void setZIP(int zip)    {        this.zip = zip;    }          public int getMessageCode()    {        return messageCode;    }          public Card getCard()    {        return card;    }            public int getZIP()    {        return zip;    }           public int getSerialNumber()    {        return serialNumber;    }           public int getFromAccount()    {        return fromAccount;    }          public int getToAccount()    {        return toAccount;    }          public Movies getAmount()    {        return amount;    }            public static final int RENT = 0;            public static final int INITIATE_RETURN = 1;           public static final int COMPLETE_RETURN = 2;            public static final int HOLD = 3;            public static final int INQUIRY = 4;        private int messageCode;            private Card card;          private int zip;           private int serialNumber;           private int fromAccount;           private int toAccount;          private Movies amount;}