package store;public class Movies{      public Movies(int dollars)    {        this(dollars, 0);    }         public Movies(int dollars, int cents)    {        this.cents = 100L * dollars + cents;    }           public Movies(Movies toCopy)    {        this.cents = toCopy.cents;    }           public String toString()    {        return "$" + cents/100 +             (cents %100 >= 10  ? "." + cents % 100 : ".0" + cents % 100);    }            public void add(Movies amountToAdd)    {        this.cents += amountToAdd.cents;    }           public void subtract(Movies amountToSubtract)    {        this.cents -= amountToSubtract.cents;    }         public boolean lessEqual(Movies compareTo)    {        return this.cents <= compareTo.cents;    }          private long cents; }